    public ClientValidator() {
    }

    /**
     * This method validates the client.
     *
     * @param client The client to be validated.
     * @return boolean
     */
    public boolean validateClient(Clients client) {
        if (client == null) {
            throw new IllegalArgumentException("Client is null!");
        }
        if (client.getName() == null || client.getName().isEmpty()) {
            throw new IllegalArgumentException("Name is null or empty!");
        }
        if (client.getAddress() == null || client.getAddress().isEmpty()) {
            throw new IllegalArgumentException("Address is null or empty!");
        }
        Pattern pattern = Pattern.compile(EMAIL_PATTERN);
        if (!pattern.matcher(client.getEmail()).matches()) {
            throw new IllegalArgumentException("Email is not a valid email!");
        }
        pattern = Pattern.compile(PHONE_PATTERN);
        if (!pattern.matcher(client.getPhone()).matches()) {
            throw new IllegalArgumentException("Phone is not a valid phone number!");
        }
        return true;
    }
